INSERT INTO films (id, name, releasedate, country, producer, lasting, description, picture) VALUES (1, 'Alien: Covenant', 'May 19th, 2017', 'USA', 'Ridley Scott', 126, 'Bound for a remote planet on the far side of the galaxy, members (Katherine Waterston, Billy Crudup) of the colony ship Covenant discover what they think to be an uncharted paradise.', '1.jpg');
INSERT INTO films (id, name, releasedate, country, producer, lasting, description, picture) VALUES (2, 'Diary of a Wimpy Kid: The Long Haul', 'May 19th, 2017', 'USA', 'David Bowers', 91, 'Young Greg Heffley is looking forward to a long summer of just hanging out, but his mother throws a monkey wrench into his plans when she forces the entire family to take a road trip for a relative''s birthday celebration', '2.jpg');
INSERT INTO films (id, name, releasedate, country, producer, lasting, description, picture) VALUES (4, 'Wakefield', 'May 19th, 2017', 'USA', 'Robin Swicord', 108, 'Howard Wakefield, a New York City lawyer, hides out in the attic of his home for weeks, coming out in the daytime when his family is gone to shower and eat. ', 'default.jpg');
INSERT INTO films (id, name, releasedate, country, producer, lasting, description, picture) VALUES (3, 'King Arthur: Legend of the Sword', 'May 12th, 2017', 'United Kingdom', 'Guy Ritchie', 128, 'After the murder of his father, young Arthur''s power-hungry uncle Vortigern seizes control of the crown. Robbed of his birthright, he grows up the hard way in the back alleys of the city, not knowing who he truly is.', 'default.jpg');
INSERT INTO films (id, name, releasedate, country, producer, lasting, description, picture) VALUES (5, 'Baahubali 2: The Conclusion', 'April 28th, 2017', 'India', 'Arka Mediaworks', 165, 'Great India Films', 'default.jpg');
INSERT INTO films (id, name, releasedate, country, producer, lasting, description, picture) VALUES (11, 'Guardians of the Galaxy Vol. 2', 'May 5th, 2017', 'USA', 'James Gunn', 135, 'Peter Quill and his fellow Guardians are hired by a powerful alien race, the Sovereign, to protect their precious batteries from invaders. ', 'default.jpg');